    @Test
    fun get_creator_address_from_signature() {
        val utils = UtilsWrapper()
        val contractAddress = "0x4D5f842b457b40A1f6685f0FB1EC7e64fB9bef89"
        val signature =
            "0x6ad8cc0922f99bbe2129a5a69d663495b885cf89de6334d57631ab44184333a41c4495c6c5b6e6312f8949169e09279b0a34f85425236d7a20d6fc62b7f162761b"

        val signMessage = "20220729" + contractAddress
        val contractCreatorAddress = utils.recover(signMessage, utils.decodeSignature(signature))

        // 0xf6a92852fb08999a85311ff8b370e796f3723afe
        println(contractCreatorAddress)
        Assertions.assertNotEquals("0x48b084d5978b739a7fe642b58ab6ce30fe747172", contractCreatorAddress)
    }

