    /**
     * https://github.com/OpenZeppelin/openzeppelin-sdk/issues/821
     *
     *  Since Solidity 0.5.2, the swarm hash may not be the last it
     *  em in the contract's bytecode (it still is on the deployedBytecode, though),
     *  as constants used by the constructor may be added after it.
     *  This means that the tryRemoveSwarmHash function from bytecode.ts will fail,
     *  since it assumes it is the last bytes sequence (note the ending dollar sign in the regex).
     *
     *  bytecode.replace(/a165627a7a72305820[a-fA-F0-9]{64}0029$/, '')
     */
    @Test
    fun contract_code_verify_with_bora() {
        val code =
            ""
        val code_without_swarmhash = code.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        // ./solc_mac --bin-runtime bora.sol ( --evm-version petersburg )
        val bin1 =
            ""
        val bin1_without_swarmhash = bin1.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        // ./solc_mac --evm-version constantinople --bin-runtime  bora.sol
        val bin2 =
            ""
        val bin2_without_swarmhash = bin2.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        Assertions.assertEquals(code_without_swarmhash, bin1_without_swarmhash)
        Assertions.assertEquals(code_without_swarmhash, bin2_without_swarmhash)
    }

    @Test
    fun contract_code_verify_with_claimswap() {
        val code =
            ""
        val code_without_swarmhash = code.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        // ./solc_mac --optimize --optimize-runs 1000  --bin-runtime ClaimSwap.sol
        val bin1 =
            ""
        val bin1_without_swarmhash = bin1.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        // When running "./solc_mac --bin-runtime ClaimSwap.sol" (without optimization and with swarm hash removed), the result is still different.
        val bin2 =
            ""
        val bin2_without_swarmhash = bin2.replace(Regex("a165627a7a72305820[a-fA-F0-9]{64}0029"), "")

        Assertions.assertEquals(code_without_swarmhash, bin1_without_swarmhash)
        Assertions.assertNotEquals(code_without_swarmhash, bin2_without_swarmhash)
    }

